
window.onload = function() {
    // 控制header、nav
    var lastScrollY = 0;
    window.addEventListener('scroll', function () {
        var st = this.scrollY;

        if (st < lastScrollY) {
            document.body.classList.remove('hideUp');
            document.getElementById('navid').style.top = '52px';
 
        } else {
            document.body.classList.add('hideUp');
            document.getElementById('navid').style.top = '0px';
        }
        lastScrollY = st;
    });

    // 控制nav a點擊

    // 1. 精選
    document.getElementById("nava1").onclick = function () {
        document.getElementById("nava1").style.borderBottom = " #fff 4px solid";
        document.getElementById("nava1").style.color = "white";

        document.getElementById("nava2").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava3").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava4").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava5").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava2").style.color = "#9d9d9d";
        document.getElementById("nava3").style.color = "#9d9d9d";
        document.getElementById("nava4").style.color = "#9d9d9d";
        document.getElementById("nava5").style.color = "#9d9d9d";
        
    };

    // 2. 新聞
    document.getElementById("nava2").onclick = function () {
        document.getElementById("nava2").style.borderBottom = " #fff 4px solid";
        document.getElementById("nava2").style.color = "white";

        document.getElementById("nava1").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava3").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava4").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava5").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava1").style.color = "#9d9d9d";
        document.getElementById("nava3").style.color = "#9d9d9d";
        document.getElementById("nava4").style.color = "#9d9d9d";
        document.getElementById("nava5").style.color = "#9d9d9d";
    };

    // 3. 運動
    document.getElementById("nava3").onclick = function () {
        document.getElementById("nava3").style.borderBottom = " #fff 4px solid";
        document.getElementById("nava3").style.color = "white";

        document.getElementById("nava2").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava1").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava4").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava5").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava2").style.color = "#9d9d9d";
        document.getElementById("nava1").style.color = "#9d9d9d";
        document.getElementById("nava4").style.color = "#9d9d9d";
        document.getElementById("nava5").style.color = "#9d9d9d";
    };

    // 4. 娛樂
    document.getElementById("nava4").onclick = function () {
        document.getElementById("nava4").style.borderBottom = " #fff 4px solid";
        document.getElementById("nava4").style.color = "white";

        document.getElementById("nava2").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava3").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava1").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava5").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava2").style.color = "#9d9d9d";
        document.getElementById("nava3").style.color = "#9d9d9d";
        document.getElementById("nava1").style.color = "#9d9d9d";
        document.getElementById("nava5").style.color = "#9d9d9d";
    };

    // 5. 音樂
    document.getElementById("nava5").onclick = function () {
        document.getElementById("nava5").style.borderBottom = " #fff 4px solid";
        document.getElementById("nava5").style.color = "white";

        document.getElementById("nava2").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava3").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava4").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava1").style.borderBottom = " #232323 solid 2px";
        document.getElementById("nava2").style.color = "#9d9d9d";
        document.getElementById("nava3").style.color = "#9d9d9d";
        document.getElementById("nava4").style.color = "#9d9d9d";
        document.getElementById("nava1").style.color = "#9d9d9d";
    };




    
}

